import styled from "styled-components/native";

export const Container = styled.ScrollView.attrs(() => ({
  // Scroll na horizontal
  horizontal: true,
  contentContainerStyle: {
    alignItems: "center",
    paddingLeft: 16
  }
}))`
  background: #1e222b;
  height: 130px;
`;
